mapper.update:
/* 공지 글 수정 */
	UPDATE 
		tb_board
	set		
		board_title = #{boardTitle}
		,board_content = #{boardContent}
		,board_add_file = #{boardAddFile}
		,board_add_file_name = #{boardAddFileName}
		,board_add_file_vol = #{boardAddFileVol}
		,update_time = CURDATE()			
	WHERE board_code = #{boardCode};
